#!/bin/bash

. ../set_mig_env.sh $*

if [ ! "$IS_PROCEED" = "true" ]; then
	return
fi

"$DB2_HOME"/bin/db2 LOAD FROM "$FILE_PATH" OF ASC MODIFIED BY TIMESTAMPFORMAT=\"DDMMYYYY\" DUMPFILE="$DUMP_PATH" IMPLIEDDECIMAL STRIPTBLANKS NULLINDCHAR=Y METHOD L \( 1 1, 2 44, 45 63, 64 66, 67 85, 86 104, 105 106, 107 121, 122 132, 133 143, 144 148, 149 149, 150 150, 151 151, 152 152, 153 162, 163 173, 174 184, 185 264 \) MESSAGES "$LOG_PATH" TEMPFILES PATH "$TEMPFILE_PATH" REPLACE INTO "$DB_SCHEMA".MIG_CA003_4_REL \(RECORD_TYPE, LMT_ID, LLP_BCA_REF_NUM, LMT_FAC_CODE, LMT_FAC_SEQ, CIF_NUMBER, ACCT_RELATIONSHIP_CODE_VALUE, GUARANTEE_AMOUNT, GUARANTEE_PERCENTAGE, SHARE_HOLDER_PERCENTAGE, HOST_ADD_SEQ_NUM, RECEIVE_MAIL_CODE, HOLD_MAIN_CODE_VALUE, NAME_ASSOC_WITH_FAC_ORDER, NAME_CONJUNCTION_POSITION, NAME_CONJUNCTION_CODE, PROFIT_RATIO, DIVIDEND_RATIO, CUSTOMER_NAME \) NONRECOVERABLE INDEXING MODE AUTOSELECT